  // test support

  // configure the response to the experian request endpoint (the same endpoint handles
  // both getting questions and submitting the response to them)
  private void setExperianMockResponse(String responseJsonString) throws JsonProcessingException {
    ObjectNode questionsSampleResponse = _mapper.readValue(responseJsonString, ObjectNode.class);
    when(_mockRestTemplate.postForObject(
            eq(FAKE_PROPERTIES.getInitialRequestEndpoint()), any(), eq(ObjectNode.class)))
        .thenReturn(questionsSampleResponse);
  }

  private IdentityVerificationQuestionsRequest createValidQuestionsRequest() {
    IdentityVerificationQuestionsRequest request = new IdentityVerificationQuestionsRequest();
    request.setOrgExternalId(FAKE_ORG_EXTERNAL_ID);
    request.setFirstName("First");
    request.setLastName("Last");
    request.setDateOfBirth("1945-06-07");
    request.setEmail("test@user.com");
    request.setPhoneNumber("800-555-1212");
    request.setStreetAddress1("1234 Main St.");
    request.setCity("Any City");
    request.setState("CA");
    request.setZip("90210");
    return request;
  }

  private IdentityVerificationAnswersRequest createValidAnswersRequest() {
    IdentityVerificationAnswersRequest request = new IdentityVerificationAnswersRequest();
    request.setOrgExternalId(FAKE_ORG_EXTERNAL_ID);
    request.setSessionId(SAMPLE_SESSION_ID);
    request.setAnswers(Arrays.asList(1, 2, 3, 4));
    return request;
  }
